import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import NavBar from "../../components/NavBar";
import "./Basket.css";

const basket = JSON.parse(sessionStorage.getItem("basket")) || [];

function formatDate() {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
}

function updateStockOnServer(sku, amount) {
  // Make the API call to update the stock on the server
  // Replace "YOUR_API_ENDPOINT" with the actual API endpoint
  fetch("https://jbcwifish5.execute-api.eu-west-1.amazonaws.com/default/updateInventoryCount", {
    method: "PUT",
    body: JSON.stringify({ sku, amount }),
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (!response.ok) {
        throw new Error("Failed to update stock on the server");
      }
    })
    .catch((error) => {
      console.error(error);
    });
}

function Basket() {

  const [products, setProducts] = useState([])
  const [basket, setBasket] = useState(JSON.parse(sessionStorage.getItem("basket")) || []);
  const [loading, setLoading] = useState(true);
  const [lowStock, setLowStock] = useState([]);
  const newLowStock = [];


  useEffect(() => {
    fetch('https://y6wf50h2e4.execute-api.eu-west-1.amazonaws.com/dev/getProductDalek')
      .then(response => response.json())
      .then(data => {
        setProducts(JSON.parse(data.body));
        console.log(products)
        setLoading(false); // Set loading to false after data is fetched and set
      })
      .catch(error => {
        console.error(error);
        setLoading(false); // Set loading to false if an error occurs
      });
  }, []);

  function CheckStockLevels() {

    
  for (let j = 0; j < basket.length; j++) {
    const basketProduct = basket[j];
    const productInBasket = products.find(
      (product) => product.sku.S === basketProduct.sku
    );

    if (productInBasket) {
      // Check if there's enough stock for the product in the basket
      if (basketProduct.amount > productInBasket.inventory_count.N) {
        const proceedWithReducedStock = window.confirm(
          "Not Enough Stock for: " +
            basketProduct.name +
            ". Being changed to: " +
            productInBasket.inventory_count.N +
            ". Do you wish to proceed?"
        );
        if (proceedWithReducedStock) {
          basketProduct.amount = productInBasket.inventory_count.N;
        } else {
          basket.splice(j, 1);
          console.log(basket);
        }
      }

      const remainingProduct = productInBasket.inventory_count.N - basketProduct.amount;
      console.log(remainingProduct);

      if (remainingProduct <= 3) {
        console.log(
          "Low Stock for: ",
          productInBasket.name.S,
          " Amount Left: ",
          remainingProduct
        );

        const itemAlreadyInLowStock = newLowStock.some(
          (item) => item.name.S === productInBasket.name.S
        );

        if (!itemAlreadyInLowStock) {
          newLowStock.push(productInBasket);
        }
      }
    }
  }
}

  function CheckOut() {
    if (basket.length !== 0) {
      const order_ID = "S2M_" + uuidv4();
      let items = [];
      let price = 0;

      CheckStockLevels();
      console.log("Outside function newLowStock: ", newLowStock)

      for (let i = 0; i < basket.length; i++) {
        items.push({ sku: basket[i].sku, name: basket[i].name, amount: basket[i].amount, price: basket[i].price});
        price += basket[i].amount * basket[i].price;
      }

      const userID = sessionStorage.getItem("name");

      // Make the API call to post the order to the server
      fetch("https://3nh8ubnqrh.execute-api.eu-west-1.amazonaws.com/default/postOrderDalek", {
        mode: "cors",
        method: "POST",
        body: JSON.stringify({
          order_ID,
          userID,
          price,
          items,
          dateOrder: formatDate(),
          completed: false,
          newLowStock: newLowStock,
          email: sessionStorage.getItem("email"),
        }),
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
            console.log(res);
            return res.json();
          }
          throw new Error("Request failed!");
        })
        .then((data) => {
          window.alert("Posted Correctly");
          console.log(data);
          sessionStorage.removeItem("basket");
          window.location.reload();
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
        });

      // Update the stock on the server for each item in the basket
      for (let i = 0; i < basket.length; i++) {
        const { sku, amount } = basket[i];
        console.log(sku, amount)
        updateStockOnServer(sku, amount);
      }
    }
  }

  function checkMaxStock(index){
    for (let i = 0; i < products.length; i++) {
      if(products[i].sku.S === basket[index].sku){
        if(products[i].inventory_count.N > basket[index].amount){
          console.log("Inside Loop")
          return true;
        }else{
          return false
        }
      }
    }
  }

  function add(i) {
    if(checkMaxStock(i)){
      console.log("Less Than")
      const updatedBasket = [...basket];
      updatedBasket[i].amount += 1;
      setBasket(updatedBasket);
      sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
    }else{
      alert("Max Amount Reached")
    }
  }

  function subtract(i) {

    const updatedBasket = [...basket];
    updatedBasket[i].amount -= 1;
    if (updatedBasket[i].amount === 0) {
      updatedBasket.splice(i, 1);
    }
    setBasket(updatedBasket);
    sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
  }

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <NavBar />
      <div className="wrapper">
        <div className="content-main-basket">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Amount</th>
                <th>Image</th>
                <th>Description</th>
                <th>Buttons</th>
              </tr>
            </thead>
            <tbody>
              {basket.map((product, index) => (
                <tr key={index}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.amount}</td>
                  <td>
                    <img src={product.image_url} id="basket-img" alt="Product 1" width="50" height="50" />
                  </td>
                  <td>{product.description}</td>
                  <td>
                    <button onClick={() => add(index)}>Add</button>
                    <button onClick={() => subtract(index)}>Subtract</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="button">
            <button id="basket-CheckOut" onClick={CheckOut}>
              Check Out
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Basket;
