import React, { useEffect, useState } from "react";

import NavBar from "../NavBar";

// import "./UpdateProduct.css";

function ChangeDetails() {
  const [user, setUser] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true); // Add a loading state

  useEffect(() => {
    fetch("https://d93b7k76j9.execute-api.eu-west-1.amazonaws.com/default/https://gj4tidlx33.execute-api.eu-west-1.amazonaws.com/default/getIndividualUser", {
    mode: "cors",
      method: "DELETE",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        userID: sessionStorage.getItem("name"),
      })
    })  
    .then((res) => {
        if (!res.ok) {
          throw new Error("Request failed with status: " + res.status);
        }
        return res.json();
      })
      .then((data) => {
        const parsedData = JSON.parse(data.body);
        console.log(parsedData)
        setUser(parsedData);
        setIsLoading(false); // Set loading to false when data is loaded
      })
      .catch((error) => {
        console.error("Error: " + error);
        setIsLoading(false); // Set loading to false on error as well
      });
  }, []);

  useEffect(() => {
    if (user.length > 0) {
      updateFormValues(currentIndex);
    }
  }, [user, currentIndex]);

  function updateFormValues(index) {
    if (user.length > 0) {
      const currentUser = user[index];
      document.getElementById("username").value = currentUser.userID.S;
      document.getElementById("email").value = currentUser.email.S;
      document.getElementById("address").value = currentUser.address.S;
    }
  }

  function changeLeft() {
    if (currentIndex === 0) {
      alert("No More Records");
    } else {
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  }

  function changeRight() {
    if (currentIndex === user.length - 1) {
      alert("No More Records");
    } else {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  }

  function handleForm(event) {
    event.preventDefault();

    const sku = document.getElementById("sku").value;

    fetch("https://gizg35elgg.execute-api.eu-west-1.amazonaws.com/default/deleteProductDalek", {
      mode: "cors",
      method: "DELETE",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        sku: sku,
      })
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      }
      throw new Error('Failed to replace product');
    })
    .then(data => {
      window.alert("Details Changed");
      document.getElementById("product-form").reset();
      window.location.reload();
    })
    .catch(error => {
      console.error('Error replacing product:', error);
    });
  }

  return (
    <div className="AddProduct">
        <NavBar />
      <div className="content-main" >
        <div className="container">
          {isLoading ? ( // Check if data is loading
            <p>Fetching products...</p> // Display loading message
          ) : (
            user.length > 0 && ( // Only render form when data is available
            <form id="product-form" onSubmit={handleForm}>
            <label>Change Details</label>
            <label>Username:</label>
            <input type="text" id="username" placeholder="product-name" className="text-input" readOnly/>
            <label>Email:</label>
            <input type="text" id="email" placeholder="product-name" className="text-input" />
            <label>Address:</label>
            <input type="float" id="address" placeholder="product-price" className="text-input" />
            <label>Password:</label>
            <input type="text" id="password" placeholder="product-description" className="text-input" />
            <button type="submit">Delete Product</button>
          </form>
            )
          )}
        </div>
      </div>
    </div>
  );
}

export default ChangeDetails;
