import React from "react";
import NavBar from "../../components/NavBar";
import "./Basket.css";

function Basket() {
  const basket = JSON.parse(sessionStorage.getItem("basket")) || [];

  return (
    <>
      <NavBar />
      <div className="content-main">
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Amount</th>
                <th>Image</th>
                <th>Description</th>
            </tr>
            </thead>
            <tbody>
            {basket.map((product, index) => (
                <tr key={index}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.amount}</td>
                <td>
                    <img src={product.image_url} alt="Product 1" width="50" height="50" />
                </td>
                <td>{product.description}</td>
                </tr>
            ))}
            </tbody>
        </table>
        <button>Check Out</button>
      </div>
    </>
  );
}

export default Basket;
