import React, {useEffect, useState} from "react";

function CreateOrder(){
  
  const [items, setItems] = useState([]);
  const [sortKey, setSortKey] = useState("date");
  const [sortOrder, setSortOrder] = useState("asc");

      useEffect(() => {
        fetch('https://1blnclstr9.execute-api.eu-west-1.amazonaws.com/default/getOrdersDalek', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Handle the response data from the Lambda function
              console.log(JSON.parse(data.body));
              setItems(JSON.parse(data.body));
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });
    }, [])    


    const handleSort = (key) => {
      if (key === sortKey) {
        setSortOrder(sortOrder === "asc" ? "desc" : "asc");
      } else {
        setSortKey(key);
        setSortOrder("asc");
      }
    };
  
    const sortedItems = items.slice().sort((a, b) => {
      const valueA = a[sortKey];
      const valueB = b[sortKey];
    
      if (valueA === undefined || valueA === null) {
        return sortOrder === "asc" ? -1 : 1;
      }
      if (valueB === undefined || valueB === null) {
        return sortOrder === "asc" ? 1 : -1;
      }
    
      // Convert numeric values to numbers for proper sorting
      const numericA = Number(valueA);
      const numericB = Number(valueB);
    
      if (!isNaN(numericA) && !isNaN(numericB)) {
        console.log("Hello Numebr")
        return sortOrder === "asc" ? numericA - numericB : numericB - numericA;
      } else {
        const stringValueA = String(valueA).toLowerCase();
        const stringValueB = String(valueB).toLowerCase();
        return sortOrder === "asc" ? stringValueA.localeCompare(stringValueB) : stringValueB.localeCompare(stringValueA);
      }
    });
  
      return(
          <div className="CreateAdmin">
              <div className="content-main">
              <table>
                <thead>
                <tr>
                    <th onClick={() => handleSort("date")}>Date</th>
                    <th>OrderID</th>
                    <th onClick={() => handleSort("price")}>Price</th>
                    <th>Items (Name, Amount)</th>
                    <th>User</th>
                    <th>Completed</th>
                </tr>
                </thead>
                <tbody>
                {sortedItems.map((order, index) => (
                    <tr key={index}>
                    <td>{order.dateOrder.S}</td>
                    <td>{order.order_ID.S}</td>
                    <td>{order.price.N}</td>
                    <td>{order.items.L.map((item) => {
                        const name = item.M.name?.S || "N/A";
                        const amount = item.M.amount?.N || "N/A";
                        return `(${name}, ${amount})`;
                      }).join(", ") || ""
                    }</td>
                    <td>{order.userID.S}</td>
                    <td><input type="checkbox" checked={order.completed}/></td>
                    </tr>
                ))}
                </tbody>
            </table>
            </div>
          </div>
      );
  
  }
  
  export default CreateOrder;
