import React from "react";
import {v4 as uuidv4} from "uuid";

import NavBar from "../../components/NavBar";
import "./Basket.css";

function Basket() {
  const basket = JSON.parse(sessionStorage.getItem("basket")) || [];

  function CheckOut(){

    console.log(basket)

    if (basket.length !== 0){

        const order_ID = "S2M_" + uuidv4()
        var items = [];
        var price = 0;

        console.log("Checking Out")

        for (let i = 0; i < basket.length; i++) {
            items.push({"name": basket[i].sku, "amount": basket[i].amount})
            price += basket[i].amount * basket[i].price
            console.log(items);
        }

        const userID = sessionStorage.getItem("name");

        fetch("https://3nh8ubnqrh.execute-api.eu-west-1.amazonaws.com/default/postOrderDalek", {
        mode: "cors",
        method: "POST",
        body: JSON.stringify({
            order_ID: order_ID, // Fix: Pass the SKU value correctly
            userID: userID,
            price: price,
            items: items,
        }),
        headers: {
            "Content-Type": "application/json",
        },
        })
        .then((res) => {
            if (res.ok) {
            console.log(res)
            return res.json();
            }
            throw new Error("Request failed!");
        })
        .then((data) => {
            window.alert("Posted Correctly");
            console.log(data);
            sessionStorage.removeItem("basket")
        })
        .catch((error) => {
            console.error("There was a problem with the fetch operation:", error);
        });
    }
}

function add(i){
    basket[i].amount += 1;
}

function subtract(i){
    basket[i].amount -= 1;
}

  return (
    <>
      <NavBar />
      <div className="wrapper">
        <div className="content-main-basket">
            <table>
                <thead>
                <tr>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Amount</th>
                    <th>Image</th>
                    <th>Description</th>
                </tr>
                </thead>
                <tbody>
                {basket.map((product, index) => (
                    <tr key={index}>
                    <td>{product.name}</td>
                    <td>{product.price}</td>
                    <td>{product.amount}</td>
                    <td>
                        <img src={product.image_url} alt="Product 1" width="50" height="50" />
                    </td>
                    <td>{product.description}</td>
                    <td>
                        <button onClick={() => add(index)}>Add</button>
                        <button onClick={() => subtract(index)}>Subtract</button></td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div className="button">
                <button id="basket-CheckOut" onClick={CheckOut}>Check Out</button>
            </div>
            
        </div>
      </div>
    </>
  );
}

export default Basket;
