import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import NavBar from "../../components/NavBar";
import "./Basket.css";

const basket = JSON.parse(sessionStorage.getItem("basket")) || [];

function formatDate() {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
}

function updateStockOnServer(sku, amount) {
  // Make the API call to update the stock on the server
  // Replace "YOUR_API_ENDPOINT" with the actual API endpoint
  fetch("https://jbcwifish5.execute-api.eu-west-1.amazonaws.com/default/updateInventoryCount", {
    method: "PUT",
    body: JSON.stringify({ sku, amount }),
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (!response.ok) {
        throw new Error("Failed to update stock on the server");
      }
    })
    .catch((error) => {
      console.error(error);
    });
}

function Basket() {

  const [products, setProducts] = useState([])
  const [basket, setBasket] = useState(JSON.parse(sessionStorage.getItem("basket")) || []);
  const [lowStock, setLowStock] = useState([])
  const [loading, setLoading] = useState(true);

  function CheckLowStock() {
    for (let i = 0; i < products.length; i++) {
      for (let j = 0; j < basket.length; j++) {
        if (basket[j].sku === products[i].sku.S) {
          if (basket[j].amount > products[i].inventory_count.N) {
            const remainingProduct = products[i].inventory_count.N - basket[j].amount;
            console.log(remainingProduct);
            if (remainingProduct < 3) {
              console.log(
                "Low Stock for: ",
                products[i].name.S,
                " Amount Left: ",
                products[i].inventory_count.N
              );
  
              // Check if an item with the same name already exists in the lowStock array
              const existingItem = lowStock.find(
                (item) => item.name.S === products[i].name.S
              );
  
              // If the item doesn't exist in the lowStock array, add it
              if (!existingItem) {
                lowStock.push(products[i]);
              }
            }
          }
        }
      }
    }
  }

  function CheckOut() {
    if (basket.length !== 0) {
      const order_ID = "S2M_" + uuidv4();
      let items = [];
      let price = 0;

      CheckLowStock();

      for (let i = 0; i < basket.length; i++) {
        items.push({ sku: basket[i].sku, name: basket[i].name, amount: basket[i].amount, price: basket[i].price});
        price += basket[i].amount * basket[i].price;
      }

      const userID = sessionStorage.getItem("name");

      // Make the API call to post the order to the server
      fetch("https://3nh8ubnqrh.execute-api.eu-west-1.amazonaws.com/default/postOrderDalek", {
        mode: "cors",
        method: "POST",
        body: JSON.stringify({
          order_ID,
          userID,
          price,
          items,
          dateOrder: formatDate(),
          completed: false,
          email: sessionStorage.getItem("email"),
          lowStock: lowStock
        }),
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
            console.log(res);
            return res.json();
          }
          throw new Error("Request failed!");
        })
        .then((data) => {
          window.alert("Posted Correctly");
          console.log(data);
          sessionStorage.removeItem("basket");
          window.location.reload();
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
        });

      // Update the stock on the server for each item in the basket
      for (let i = 0; i < basket.length; i++) {
        const { sku, amount } = basket[i];
        console.log(sku, amount)
        updateStockOnServer(sku, amount);
      }
    }
  }

  function checkMaxStock(index){
    for (let i = 0; i < products.length; i++) {
      if(products[i].sku.S === basket[index].sku){
        if(products[i].inventory_count.N > basket[index].amount){
          console.log("Inside Loop")
          return true;
        }else{
          return false
        }
      }
    }
  }

  function add(i) {
    if(checkMaxStock(i)){
      console.log("Less Than")
      const updatedBasket = [...basket];
      updatedBasket[i].amount += 1;
      setBasket(updatedBasket);
      sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
    }else{
      alert("Max Amount Reached")
    }
  }

  function subtract(i) {

    const updatedBasket = [...basket];
    updatedBasket[i].amount -= 1;
    if (updatedBasket[i].amount === 0) {
      updatedBasket.splice(i, 1);
    }
    setBasket(updatedBasket);
    sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
  }

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <NavBar />
      <div className="wrapper">
        <div className="content-main-basket">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Amount</th>
                <th>Image</th>
                <th>Description</th>
                <th>Buttons</th>
              </tr>
            </thead>
            <tbody>
              {basket.map((product, index) => (
                <tr key={index}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.amount}</td>
                  <td>
                    <img src={product.image_url} id="basket-img" alt="Product 1" width="50" height="50" />
                  </td>
                  <td>{product.description}</td>
                  <td>
                    <button onClick={() => add(index)}>Add</button>
                    <button onClick={() => subtract(index)}>Subtract</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="button">
            <button id="basket-CheckOut" onClick={CheckOut}>
              Check Out
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Basket;
