import React, {useEffect, useState} from "react";

function CreateOrder(){
  
  const [originalData, setOriginalData] = useState([]);
  const [itemsData, setItemsData] = useState([]);
  const [currentItemIndex, setCurrentItemIndex] = useState(0);
  const [dataLoaded, setDataLoaded] = useState(false);

      useEffect(() => {
        fetch('https://1blnclstr9.execute-api.eu-west-1.amazonaws.com/default/getOrdersDalek', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Store the entire API response in originalData state
              setOriginalData(JSON.parse(data.body));

              // Format the data here (using only name and amount for itemsData)
              const formattedItems = JSON.parse(data.body).map(item => ({
                name: item.M.name.S,
                amount: item.M.amount.N,
              }));

              // Set the formatted items in the itemsData state
              setItemsData(formattedItems);
              setDataLoaded(true);
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });
    }, [])

    const formatItem = (item) => {
      if (!item || !item.M || !item.M.name || !item.M.amount) {
        return ""; // Return an empty string if the item or its properties are undefined
      }
      
      const name = item.M.name.S; // Access the 'name' property inside the item
      const amount = item.M.amount.N; // Access the 'amount' property inside the item
      return `(${name}, ${amount})`;
    };
    
    // Use the 'formatItem' function to format each item and join them with a comma
    const formattedItems = items.map((item) => formatItem(item)).join(", ");
    
  
      return(
          <div className="CreateAdmin">
              <div className="content-main">
                <form id="create-order">
                {dataLoaded && (
                  <>
                    <label>Date:</label>
                    <input type="text" id="date" placeholder="Date" className="text-input" value={items[currentItemIndex]?.dateOrder?.S || ""}/>
                    <label>Price:</label>
                    <input type="text" id="price" placeholder="Price" className="text-input" value={items[currentItemIndex]?.price?.N || ""}/>
                    <label>Items:</label>
                    <input type="text" id="items" placeholder="Items" className="text-input" value={{formattedItems}}/>
                    <label>User:</label>
                    <input type="text" id="user" placeholder="user" className="text-input"/>
                    <label>Order Code:</label>
                    <input type="text" id="orderCode" placeholder="OrderCode" className="text-input"/>
                    <label>Address:</label>
                    <input type="text" id="address" placeholder="Address" className="text-input"/>
                  </>
)}
              </form>
            </div>
          </div>
      );
  
  }
  
  export default CreateOrder;
