import React, {useEffect, useState} from "react";

function CreateOrder(){
  
  const [items, setItems] = useState([]);
  const [currentItemIndex, setCurrentItemIndex] = useState(0);
  const [dataLoaded, setDataLoaded] = useState(false);

      useEffect(() => {
        fetch('https://1blnclstr9.execute-api.eu-west-1.amazonaws.com/default/getOrdersDalek', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Handle the response data from the Lambda function
              console.log(JSON.parse(data.body));
              setItems(JSON.parse(data.body));
              setDataLoaded(true);
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });
    }, [])    
  
      return(
          <div className="CreateAdmin">
              <div className="content-main">
                <form id="create-order">
                {dataLoaded && (
                  <>
                    <label>Date:</label>
                    <input type="text" id="date" placeholder="Date" className="text-input" value={items[currentItemIndex]?.dateOrder?.S || ""}/>
                    <label>Price:</label>
                    <input type="text" id="price" placeholder="Price" className="text-input" value={items[currentItemIndex]?.price?.N || ""}/>
                    <label>Items:</label>
                    <input type="text" id="items" placeholder="Items" className="text-input" value={items[currentItemIndex]?.items?.L.amount.S || ""}/>
                    <label>User:</label>
                    <input type="text" id="user" placeholder="user" className="text-input"/>
                    <label>Order Code:</label>
                    <input type="text" id="orderCode" placeholder="OrderCode" className="text-input"/>
                    <label>Address:</label>
                    <input type="text" id="address" placeholder="Address" className="text-input"/>
                  </>
)}
              </form>
            </div>
          </div>
      );
  
  }
  
  export default CreateOrder;
