import React, {useEffect, useState} from 'react';

import ProductShopPage from "../components/Users/ProductShopPage";
import NavBar from "../components/NavBar";

import "./shop.css";

function Shop() {

  const [items, setItems] = useState([]);
  const filteredItems = items.filter((item) => item.inventory_count.N !== "0");

    useEffect(() => {
        fetch('https://y6wf50h2e4.execute-api.eu-west-1.amazonaws.com/dev/getProductDalek', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Handle the response data from the Lambda function
              setItems(JSON.parse(data.body));
              console.log(JSON.parse(data.body));
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });

    }, [])

    return(
        <>
            <div className="Shop">
                <NavBar/>
                <div className="wrapper-shop">
                  {filteredItems.length === 0 ? (
                    <h2>No Stock Available</h2>
                  ) : (filteredItems.map((item) => (
                    <ProductShopPage key={item.sku.S} items={item} />
                  )))}  
                </div>  
            </div>   
        </>    
    ); 
} 

export default Shop;
