import React, {useEffect, useState} from "react";

function CreateOrder(){
  
  const [items, setItems] = useState([]);
  const [sortKey, setSortKey] = useState("date"); // Default sort key is 'id'
  const [sortOrder, setSortOrder] = useState("asc"); // Default sort order is 'asc'


      useEffect(() => {
        fetch('https://1blnclstr9.execute-api.eu-west-1.amazonaws.com/default/getOrdersDalek', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Handle the response data from the Lambda function
              console.log(JSON.parse(data.body));
              setItems(JSON.parse(data.body));
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });
    }, [])    

    const handleSort = (key) => {
      console.log("Sorting")
      // If the same key is clicked, reverse the sort order
      if (key === sortKey) {
        setSortOrder(sortOrder === "asc" ? "desc" : "asc");
        console.log("Sort Order: ", sortOrder)
      } else {
        // If a different key is clicked, set it as the new sort key
        setSortKey(key);
        setSortOrder("asc");
        console.log("Key: ", key)
      }
    };
  
    const sortedItems = items.sort((a, b) => {
      const valueA = a[sortKey];
      const valueB = b[sortKey];
    
      if (typeof valueA === "string" && typeof valueB === "string") {
        // If both values are strings, use localeCompare for string comparison
        return sortOrder === "asc" ? valueA.localeCompare(valueB) : valueB.localeCompare(valueA);
      } else if (!isNaN(valueA) && !isNaN(valueB)) {
        return sortOrder === "asc" ? parseFloat(valueA) - parseFloat(valueB) : parseFloat(valueB) - parseFloat(valueA);
      } else if (typeof valueA === "boolean" && typeof valueB === "boolean") {
        // If both values are booleans, compare based on boolean values (true first)
        return sortOrder === "asc" ? (valueA ? -1 : 1) : valueA ? 1 : -1;
      } else {
        // For other data types or mismatched types, keep the current order
        return 0;
      }
    });
  
      return(
          <div className="CreateAdmin">
              <div className="content-main">
              <table>
                <thead>
                <tr>
                    <th onClick={() => handleSort("date")}>Date</th>
                    <th>OrderID</th>
                    <th onClick={() => handleSort("price")}>Price</th>
                    <th>Items (Name, Amount)</th>
                    <th>User</th>
                    <th>Completed</th>
                </tr>
                </thead>
                <tbody>
                {sortedItems.map((order, index) => (
                    <tr key={index}>
                    <td>{order.dateOrder.S}</td>
                    <td>{order.order_ID.S}</td>
                    <td>{order.price.N}</td>
                    <td>{order.items.L.map((item) => {
                        const name = item.M.name?.S || "N/A";
                        const amount = item.M.amount?.N || "N/A";
                        return `(${name}, ${amount})`;
                      }).join(", ") || ""
                    }</td>
                    <td>{order.userID.S}</td>
                    <td><input type="checkbox" checked={order.completed}/></td>
                    </tr>
                ))}
                </tbody>
            </table>
            </div>
          </div>
      );
  
  }
  
  export default CreateOrder;
