import React, {useEffect} from "react";
import { Link } from 'react-router-dom';

import "./login.css";

import NavBar from "../components/NavBar";

function Login(){


    useEffect(() => {
        const form = document.getElementById("login");
        form.addEventListener("submit", handleForm);
        return () => {
          form.removeEventListener("submit", handleForm);
        };
      }, []);
    
      function handleForm(event) {

        event.preventDefault();
        
        console.log(event.target)

        if(event.target.name === "create-user"){
          window.location.href= "/createUser"
        }

        if(event.target.name === "login"){

          const username = document.getElementById("username").value;
          const password1 = document.getElementById("password").value;
      
          if (!username || !password1) {
            window.alert("All fields are required.");
            return;
          }
      
          fetch(process.env.REACT_APP_API + "/login", {
            mode: "cors", 
            method:"POST", 
            body: JSON.stringify({
              name: username, 
              password: password1
            }),
            headers: {
              'Content-Type': 'application/json',
            }
          })
          .then(res => {
            if (res.ok) {
              return res.json();
            }
          })
          .then(data => {
              if(data.error){
                  alert(data.error)
              }
              if(data.isAdmin){
                  document.getElementById("login").reset()
                  window.location.href = '/admin-console';
              }
              else{
                  document.getElementById("login").reset()
                  window.location.href = '/shop';
              }
          })
          .catch(error => {
            console.error('There was a problem with the fetch operation:', error);
          });
        }
      }
    
        return(
            <div className="Login">
                <NavBar />
                <div className="content-main">
                  <form id="login">
                    <label>Username:</label>
                    <input type="text" id="username" placeholder="Username" className="text-input"/>
                    <label>Password:</label>
                    <input type="password" id="password" placeholder="Confirm Password" className="text-input"/>
                    <button type="submit" name="login">Login</button>
                    <Link to="/createUser">
                      <button>Create User</button>
                    </Link>
                </form>
              </div>
            </div>
        );
    
    }

export default Login;
