import React, { useEffect, useState } from 'react';
import { NavLink } from "react-router-dom";

import "./NavBar.css"

function NavBar() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch('/session') // Send a GET request to the /session endpoint
      .then(response => response.json())
      .then(data => {
        setUser(data.user); // Set the retrieved user data in the state
      })
      .catch(error => {
        console.error('Error fetching session data:', error);
      });
  }, []);

  return (
    <div className="nav-bar">
      <div className="links-left">
        <h1>Sense2Me</h1>
        <h2><NavLink to='/' className="react-link" activeClassName="active">Home</NavLink></h2>
        <h2><NavLink to='/shop' className="react-link" activeClassName="react-link-disabled">Shop</NavLink></h2>
        <h2><NavLink to='/contact' className="react-link" activeClassName="react-link-disabled">Contact Us</NavLink></h2>
      </div>
      <div className="link-right">
        {user ? (
          <h2>Welcome, {user.user}</h2>
        ) : (
          <h2>
            <NavLink to="/login" activeClassName="react-link-disabled">
              Login
            </NavLink>
          </h2>
        )}
      </div>
    </div>
  );
}

export default NavBar;
