import React from "react";
import "./ProductDetailsShop.css"

function ProductDetailsShop(props){

    function addToBasket(){

        const basket = []
        var checker = 0

        if(basket.length === 0){
            console.log("Test 2: Does it enter the Basket.Lenght == 0")
            basket.push({sku:props.items.items.sku.S, 
                price: props.items.items.price.N, 
                name: props.items.items.name.S,
                image_url: props.items.items.image_url.S,
                description: props.items.items.description.S,
                amount: 1})
        }
        if(basket.length !== 0){
            console.log("Test 1: Does it Enter the Basket Length is greater than 1", basket.length)
            for(let i = 0; i < basket.length; i++){
                console.log("This is the i Value before any logic: ", i)
                console.log("Product SKU in Basket", basket[i].sku, " Product SKU in New Product", props.items.items.sku.S)
                if(basket[i].sku === props.items.items.sku.S){
                    basket[i].amount = basket[i].amount + 1;
                }else{
                    checker += 1;
                }
                console.log("Test 4: Value of Checker", checker)
            }

            if(checker !== basket.length){
                basket.push({sku:props.items.items.sku.S, 
                    price: props.items.items.price.N, 
                    name: props.items.items.name.S,
                    image_url: props.items.items.image_url.S,
                    description: props.items.items.description.S,
                    amount: 1})
            }
        }
        console.log(basket)
    }

    return(

        <div className="product-details">
            <h3 className="title">{props.items.items.name.S}</h3>
            <div className="price-wrapper" onClick={addToBasket}>
                <ion-icon name="add-circle-outline"></ion-icon>
                <h3 className="price-tag">{props.items.items.price.N}</h3>
            </div>
        </div>

    );

}

export default ProductDetailsShop;
