import React, {useEffect, useState} from 'react';
import {Link} from "react-router-dom";

import ProductShopPage from "../components/Users/ProductShopPage";
import NavBar from "../components/NavBar";

import "./shop.css";

function Shop() {

  const [items, setItems] = useState([]);

    useEffect(() => {
        fetch('https://y6wf50h2e4.execute-api.eu-west-1.amazonaws.com/dev', {
            method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
            headers: {
              'Content-Type': 'application/json',
              // Additional headers if required
            },
          })
            .then(response => response.json())
            .then(data => {
              // Handle the response data from the Lambda function
              setItems(data);
              console.log(data);
            })
            .catch(error => {
              // Handle any errors that occurred during the request
              console.error(error);
            });
    }, [])

    return(
        <>
            <div className="Shop">
                <NavBar/>
                <div className="wrapper-shop">  
                {items.map((item) => (
                    <Link key={item.name} to={{ pathname: "/product", state: { item } }} className="product-link">
                        <ProductShopPage items={item} />
                    </Link>
                    ))}
                </div>  
            </div>   
        </>    
    ); 
} 

export default Shop;
