import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import NavBar from "../../components/NavBar";
import "./Basket.css";

const basket = JSON.parse(sessionStorage.getItem("basket")) || [];
const [products, setProducts] = useState([])

function formatDate() {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
}

function updateStockOnServer(sku, amount) {
  // Make the API call to update the stock on the server
  // Replace "YOUR_API_ENDPOINT" with the actual API endpoint
  fetch("https://jbcwifish5.execute-api.eu-west-1.amazonaws.com/default/updateInventoryCount", {
    method: "PUT",
    body: JSON.stringify({ sku, amount }),
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (!response.ok) {
        throw new Error("Failed to update stock on the server");
      }
    })
    .catch((error) => {
      console.error(error);
    });
}

function CheckStockLevels(){
  for (let i = 0; i < products.length; i++) {
    setTimeout(() => {
      console.log("Top Nest")
    }, 2000);
    for (let j = 0; j < basket.length; j++) {
      setTimeout(() => {
        console.log("Bottom Nest Nest")
      }, 2000);
      if(basket[j].sku === products[i].sku){
        setTimeout(() => {
          console.log("Same Sku's")
        }, 2000);
        if(basket[j].amount > products[i].inventory_count){
          setTimeout(() => {
            console.log("Amount Greater than Stock Level")
          }, 2000);
          alert("Not Enough Stock for: " + basket[j].name)
          basket[j].amount = products[i].inventory_count;
        }
      }
    }
  }
}

function Basket() {

  const [basket, setBasket] = useState(JSON.parse(sessionStorage.getItem("basket")) || []);

  useEffect(() => {
    fetch('https://y6wf50h2e4.execute-api.eu-west-1.amazonaws.com/dev/getProductDalek', {
        method: 'GET', // or 'GET', 'PUT', 'DELETE' depending on your Lambda function configuration
        headers: {
          'Content-Type': 'application/json',
          // Additional headers if required
        },
      })
        .then(response => response.json())
        .then(data => {
          // Handle the response data from the Lambda function
          setProducts(JSON.parse(data.body));
          console.log(JSON.parse(data.body));
        })
        .catch(error => {
          // Handle any errors that occurred during the request
          console.error(error);
        });

        CheckStockLevels()

}, [CheckStockLevels()])

  function CheckOut() {
    if (basket.length !== 0) {
      const order_ID = "S2M_" + uuidv4();
      let items = [];
      let price = 0;

      for (let i = 0; i < basket.length; i++) {
        items.push({ name: basket[i].sku, amount: basket[i].amount });
        price += basket[i].amount * basket[i].price;
      }

      const userID = sessionStorage.getItem("name");

      // Make the API call to post the order to the server
      fetch("https://3nh8ubnqrh.execute-api.eu-west-1.amazonaws.com/default/postOrderDalek", {
        mode: "cors",
        method: "POST",
        body: JSON.stringify({
          order_ID,
          userID,
          price,
          items,
          dateOrder: formatDate(),
          completed: false,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
            console.log(res);
            return res.json();
          }
          throw new Error("Request failed!");
        })
        .then((data) => {
          window.alert("Posted Correctly");
          console.log(data);
          sessionStorage.removeItem("basket");
          window.location.reload();
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
        });

      // Update the stock on the server for each item in the basket
      for (let i = 0; i < basket.length; i++) {
        const { sku, amount } = basket[i];
        console.log(sku, amount)
        updateStockOnServer(sku, amount);
      }
    }
  }

  function add(i) {
    const updatedBasket = [...basket];
    updatedBasket[i].amount += 1;
    setBasket(updatedBasket);
    sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
  }

  function subtract(i) {
    const updatedBasket = [...basket];
    updatedBasket[i].amount -= 1;
    if (updatedBasket[i].amount === 0) {
      updatedBasket.splice(i, 1);
    }
    setBasket(updatedBasket);
    sessionStorage.setItem("basket", JSON.stringify(updatedBasket));
  }

  return (
    <>
      <NavBar />
      <div className="wrapper">
        <div className="content-main-basket">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
                <th>Amount</th>
                <th>Image</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {basket.map((product, index) => (
                <tr key={index}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.amount}</td>
                  <td>
                    <img src={product.image_url} id="basket-img" alt="Product 1" width="50" height="50" />
                  </td>
                  <td>{product.description}</td>
                  <td>
                    <button onClick={() => add(index)}>Add</button>
                    <button onClick={() => subtract(index)}>Subtract</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="button">
            <button id="basket-CheckOut" onClick={CheckOut}>
              Check Out
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Basket;
